    let temp;
    let n = arr.length;

    // swap all corners of the layer
    temp = arr[i][n - 1 - j];
    arr[i][n - 1 - j] = arr[i][j];
    arr[i][j] = arr[n - 1 - i][j];
    arr[n - 1 - i][j] = arr[n - 1 - i][n - 1 - j];
    arr[n - 1 - i][n - 1 - j] = temp;

    // swap other elements in the layer

    // swap top and right
    for (let k = i+1; k < n - 1 - i; k++) {
        temp = arr[k][n - 1 - j];
        arr[k][n - 1 - j] = arr[i][k];
        arr[i][k] = temp;
    }

    // swap top and bottom
