    let sortedSongDurations = [];
    for (let i = 0; i < songDurations.length; i++) {
        sortedSongDurations.push(songDurations[i]);
    }

    // first sort the songDurations

    mergeSort(sortedSongDurations);

    // loop through the sortedSongDurations from the back and then do a binary search so that
    // the sum of the pair is combinedDuration, so that the time complexity is n * log(n)
    // n for looping through list, log(n) for binary search

    for (let j = sortedSongDurations.length - 1; j >0; j--) {
        if (sortedSongDurations[j] < combinedDuration) {
            let remainingTime = combinedDuration - sortedSongDurations[j];
            let match = binarySearch(sortedSongDurations, remainingTime, 0, j - 1);
            if (match) {
